import './home.css'
import {images} from '../../img/images'
import React, { useState } from 'react'
import Service from '../Service/Service'



export default function Home() {
    const [activeIndex, setActiveIndex] = useState(0);
    const [activeShape,setActiveShape] = useState(images.x);

    const [
        active = {
            activeIndex,
            activeShapeImg,
            activeShapeName
        },
        setActive
    ] = useState(
        {
            activeIndex:0,
            activeShapeImg:images.x,
            activeShapeName:'x'
        }
    );


    let changeIndex = (index) => {
        index === activeIndex ? setActiveIndex(null) : setActiveIndex(index);
    }
    let changeShape = (index) => {
        switch (index) {
            case 0:
                setActiveShape(images.x);
                break;
            case 1:
                setActiveShape(images.o);
                break;
            case 2:
                setActiveShape(images.cu);
                break;
            case 3:
                setActiveShape(images.c);
                break;
            default:
                break;
        }
    }


    let clickHandler = (index) => {
        changeIndex(index);
        changeShape(index);
    }
    



    return (
        <div className='home'>

            <div className='shapes'>
                <img 
                className='shape-1'
                src={activeShape}
                alt='active shape'
                />
            </div>


            <ul className='home__service-list'>
                <li>
                    <Service
                        title='web-design'
                        subtitle1='
                Сайт — это не продукт ради продукта, а составляющая ва
                шего общего бизнес-концепта.
                '
                        subtitle2='
                Мы понимаем это и делаем такие сайты, 
                которые помогают сделать 
                ваш бизнес лучше.
                '
                        activeIndex={activeIndex}
                        index={0}
                        onClick={clickHandler}
                    />
                </li>
                <li>
                    <Service title='web-design'
                        subtitle1='
                Сайт — это не продукт ради продукта, а составляющая вашего общего бизнес-концепта.
                '
                        subtitle2='
                Мы понимаем это и делаем такие сайты, которые помогают сделать ваш бизнес лучше.
                '
                activeIndex={activeIndex}
                        index={1}
                        onClick={clickHandler}
                    />
                </li>
                <li>
                    <Service title='web-design'
                        subtitle1='
                Сайт — это не продукт ради продукта, а составляющая вашего общего бизнес-концепта.
                '
                        subtitle2='
                Мы понимаем это и делаем такие сайты, которые помогают сделать ваш бизнес лучше.
                '
                activeIndex={activeIndex}
                        index={2}
                        onClick={clickHandler}
                    />
                </li>
                <li>
                    <Service title='web-design'
                        subtitle1='
                Сайт — это не продукт ради продукта, а составляющая вашего общего бизнес-концепта.
                '
                        subtitle2='
                Мы понимаем это и делаем такие сайты, которые помогают сделать ваш бизнес лучше.
                '
                activeIndex={activeIndex}
                        index={3}
                        onClick={clickHandler}
                    />
                </li>
                <li>
                    <Service title='web-design'
                        subtitle1='
                Сайт — это не продукт ради продукта, а составляющая вашего общего бизнес-концепта.
                '
                        subtitle2='
                Мы понимаем это и делаем такие сайты, которые помогают сделать ваш бизнес лучше.
                '
                activeIndex={activeIndex}
                        index={4}
                        onClick={clickHandler}
                    />
                </li>
            </ul>
        </div>
    )
}
